// Enhanced validation and data preparation for AI analysis
const data = $json;
const sessionId = `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

console.log(`üîç [${sessionId}] Processing AI analysis request:`, JSON.stringify(data, null, 2));

// Enhanced validation with detailed error messages
if (!data.accessToken) {
  return {
    json: {
      status: 'error',
      message: 'Access token is required for AI analysis',
      sessionId: sessionId,
      timestamp: new Date().toISOString(),
      receivedData: data
    }
  };
}

if (!data.creativeId) {
  return {
    json: {
      status: 'error',
      message: 'Creative ID is required for AI analysis',
      sessionId: sessionId,
      timestamp: new Date().toISOString(),
      receivedData: data
    }
  };
}

if (!data.adAccountId) {
  return {
    json: {
      status: 'error',
      message: 'Ad account ID is required for AI analysis',
      sessionId: sessionId,
      timestamp: new Date().toISOString(),
      receivedData: data
    }
  };
}

// Prepare enhanced data for AI analysis
const enhancedData = {
  ...data,
  sessionId: sessionId,
  workflowVersion: 'complete-v1.0',
  receivedAt: new Date().toISOString(),
  processingStartTime: Date.now(),
  analysisType: 'ai_creative_analysis'
};

console.log(`‚úÖ [${sessionId}] Validation passed, proceeding with AI analysis`);
console.log(`üîë Access token: ${enhancedData.accessToken.substring(0, 10)}...`);
console.log(`üéØ Creative ID: ${enhancedData.creativeId}`);
console.log(`üè¢ Ad Account ID: ${enhancedData.adAccountId}`);

return {
  json: enhancedData
};

// Extract and prepare creative data for AI analysis
const item = $json;
const webhookData = $('ai-validation').first().json;
const sessionId = webhookData.sessionId;

console.log(`üîç [${sessionId}] Received creative data:`, JSON.stringify(item, null, 2));

// Enhanced error handling
if (!item) {
  return {
    json: {
      error: "No response data received",
      message: "Fetch Creative node returned no data",
      sessionId: sessionId,
      creativeId: webhookData.creativeId || 'unknown',
      timestamp: new Date().toISOString()
    }
  };
}

if (item.statusCode && item.statusCode >= 400) {
  return {
    json: {
      error: `API call failed with status ${item.statusCode}`,
      message: item.statusText || "HTTP request failed",
      sessionId: sessionId,
      creativeId: webhookData.creativeId || 'unknown',
      response: item,
      timestamp: new Date().toISOString()
    }
  };
}

// Extract creative data with fallback strategies
let creativeData = null;
if (item.creative) {
  creativeData = item.creative;
} else if (item.data && item.data.creative) {
  creativeData = item.data.creative;
} else if (item.data) {
  creativeData = item.data;
} else if (item.json && item.json.creative) {
  creativeData = item.json.creative;
} else if (item.json) {
  creativeData = item.json;
} else {
  creativeData = item;
}

console.log(`üîç [${sessionId}] Extracted creative data:`, JSON.stringify(creativeData, null, 2));

if (!creativeData || !creativeData.id) {
  return {
    json: {
      error: "No valid creative data found",
      message: "Could not extract creative information from API response",
      sessionId: sessionId,
      creativeId: webhookData.creativeId || 'unknown',
      receivedData: item,
      timestamp: new Date().toISOString()
    }
  };
}

// Extract image URL for AI analysis
let imageUrl = creativeData.image_url || 
               creativeData.picture || 
               creativeData.thumbnail_url ||
               creativeData.object_story_spec?.link_data?.picture ||
               creativeData.object_story_spec?.video_data?.image_url ||
               creativeData.object_story_spec?.photo_data?.url ||
               creativeData.object_story_spec?.link_data?.image_url ||
               creativeData.object_story_spec?.link_data?.child_attachments?.[0]?.image_url ||
               creativeData.object_story_spec?.link_data?.child_attachments?.[0]?.media?.image_url;

console.log(`üîç [${sessionId}] Extracted image URL:`, imageUrl);

if (!imageUrl) {
  return {
    json: {
      error: "No image URL found",
      message: "Creative has no image content for AI analysis",
      sessionId: sessionId,
      creativeId: creativeData.id || 'unknown',
      availableFields: Object.keys(creativeData),
      timestamp: new Date().toISOString()
    }
  };
}

// Prepare data for AI analysis
const aiAnalysisData = {
  id: creativeData.id,
  name: creativeData.name || 'Unnamed Creative',
  status: creativeData.status,
  imageUrl: imageUrl,
  image_hash: creativeData.image_hash || null,
  body: creativeData.body || creativeData.object_story_spec?.link_data?.message || '',
  title: creativeData.title || creativeData.object_story_spec?.link_data?.name || '',
  callToAction: creativeData.call_to_action_type || creativeData.object_story_spec?.link_data?.call_to_action?.type || '',
  creativeType: creativeData.creative_type || 'image',
  analysisContext: {
    adAccountId: webhookData.adAccountId,
    accessToken: webhookData.accessToken,
    creativeId: webhookData.creativeId,
    sessionId: sessionId
  },
  metadata: {
    originalData: creativeData,
    extractedAt: new Date().toISOString()
  }
};

console.log(`‚úÖ [${sessionId}] Successfully prepared data for AI analysis`);

return {
  json: aiAnalysisData
};

// Build tokenized URL for AI analysis
const creative = $json;
const sessionId = creative.analysisContext.sessionId;

console.log(`üîç [${sessionId}] Building tokenized URL for AI analysis:`, JSON.stringify(creative, null, 2));

if (!creative || !creative.imageUrl) {
  return {
    json: {
      error: "Missing creative data or image URL",
      message: "Cannot build tokenized URL without image URL",
      sessionId: sessionId,
      receivedData: creative,
      timestamp: new Date().toISOString()
    }
  };
}

if (!creative.analysisContext || !creative.analysisContext.accessToken) {
  return {
    json: {
      error: "Missing access token",
      message: "Cannot build tokenized URL without access token",
      sessionId: sessionId,
      creative: creative,
      timestamp: new Date().toISOString()
    }
  };
}

// Enhanced URL tokenization for Facebook CDN
function appendAccessTokenToImageUrl(imageUrl, accessToken) {
  if (!imageUrl || !accessToken) return imageUrl;

  const facebookDomains = [
    'fbcdn.net', 'fbsbx.com', 'facebook.com', 'fb.com',
    'instagram.com', 'cdninstagram.com', 'igcdn.com'
  ];

  const isFacebookCDN = facebookDomains.some(domain => imageUrl.includes(domain));

  if (!isFacebookCDN) {
    console.log(`‚ÑπÔ∏è [${sessionId}] URL is not from Facebook CDN, skipping tokenization:`, imageUrl);
    return imageUrl;
  }

  if (imageUrl.includes('access_token=')) {
    console.log(`‚ÑπÔ∏è [${sessionId}] URL already has access token, skipping tokenization`);
    return imageUrl;
  }

  const separator = imageUrl.includes('?') ? '&' : '?';
  const tokenizedUrl = `${imageUrl}${separator}access_token=${accessToken}`;

  console.log(`‚úÖ [${sessionId}] Successfully tokenized URL for AI analysis`);
  return tokenizedUrl;
}

const tokenizedImageUrl = appendAccessTokenToImageUrl(
  creative.imageUrl, 
  creative.analysisContext.accessToken
);

// Enhanced data for AI analysis
const enhancedData = {
  ...creative,
  tokenizedImageUrl: tokenizedImageUrl,
  processingMetadata: {
    processingId: `${creative.id}_${Date.now()}`,
    processingStartTime: Date.now(),
    originalImageUrl: creative.imageUrl,
    tokenizedImageUrl: tokenizedImageUrl,
    tokenizationSuccess: tokenizedImageUrl !== creative.imageUrl,
    facebookCDN: creative.imageUrl.includes('fbcdn.net') || creative.imageUrl.includes('facebook.com')
  }
};

console.log(`üîó [${sessionId}] Built tokenized URL:`, tokenizedImageUrl);
return {
  json: enhancedData
};

