// Process AI response and prepare final analysis
const creative = $json;
const sessionId = creative.analysisContext?.sessionId || 'unknown';
let aiResponse;

console.log(`üîç [${sessionId}] Processing AI response:`, JSON.stringify(creative, null, 2));

try {
  // Extract response text with fallback strategies
  let responseText = '';
  if (creative.choices && creative.choices[0]) {
    responseText = creative.choices[0].message?.content || creative.choices[0].text || '';
  } else if (creative.message?.content) {
    responseText = creative.message.content;
  } else if (typeof creative === 'string') {
    responseText = creative;
  } else if (creative.content) {
    responseText = creative.content;
  } else {
    responseText = JSON.stringify(creative);
  }

  console.log(`üîç [${sessionId}] Raw AI response text:`, responseText);

  // Clean and parse the response
  const cleanedText = responseText.trim().replace(/```json\n?/g, '').replace(/```\n?/g, '');
  aiResponse = JSON.parse(cleanedText);

  console.log(`‚úÖ [${sessionId}] Successfully parsed AI response:`, JSON.stringify(aiResponse, null, 2));

} catch (error) {
  console.error(`‚ùå [${sessionId}] Failed to parse AI response:`, error);
  console.log(`üîç [${sessionId}] Raw response that failed to parse:`, responseText);

  // Enhanced fallback with comprehensive default structure
  aiResponse = {
    score: {
      overall: 0,
      dimensions: { 
        clarity: 0, 
        text_density: 0, 
        brand: 0, 
        value_prop: 0, 
        cta: 0, 
        contrast: 0, 
        thumbnail: 0 
      }
    },
    insights: {
      strengths: ["AI analysis failed to process"],
      issues: ["Response parsing error"],
      suggestions: ["Retry analysis", "Check AI service status"]
    },
    adVariations: [
      {
        variation: 1,
        description: "Default variation due to analysis failure",
        keyChanges: ["Retry analysis"],
        expectedImprovement: "Unknown"
      }
    ],
    flags: { 
      compliance: ["analysis_error"],
      performance: ["unknown"],
      optimization: ["retry_needed"]
    }
  };
}

// Enhanced score validation and normalization
function clampScore(score) {
  const numScore = Number(score) || 0;
  return Math.max(0, Math.min(100, Math.round(numScore)));
}

const dimensions = {
  clarity: clampScore(aiResponse.score?.dimensions?.clarity),
  text_density: clampScore(aiResponse.score?.dimensions?.text_density),
  brand: clampScore(aiResponse.score?.dimensions?.brand),
  value_prop: clampScore(aiResponse.score?.dimensions?.value_prop),
  cta: clampScore(aiResponse.score?.dimensions?.cta),
  contrast: clampScore(aiResponse.score?.dimensions?.contrast),
  thumbnail: clampScore(aiResponse.score?.dimensions?.thumbnail)
};

// Enhanced scoring algorithm with industry best practices
const weights = { 
  clarity: 0.20, 
  text_density: 0.15, 
  brand: 0.15, 
  value_prop: 0.20, 
  cta: 0.15, 
  contrast: 0.10, 
  thumbnail: 0.05 
};

const calculatedOverall = Math.round(
  Object.entries(dimensions).reduce((sum, [key, score]) => {
    return sum + (score * weights[key]);
  }, 0)
);

const overallScore = aiResponse.score?.overall ? 
  clampScore(aiResponse.score.overall) : 
  calculatedOverall;

// Get original creative data for enhanced response
const originalCreative = $('build-tokenized-urls').first().json;

// Enhanced response with comprehensive data
const enhancedResponse = {
  creativeId: originalCreative.id,
  adAccountId: originalCreative.analysisContext.adAccountId,
  imageUrl: originalCreative.imageUrl,
  imageHash: originalCreative.image_hash,
  model: 'gpt-4o',
  score: { 
    overall: overallScore, 
    dimensions,
    calculated: calculatedOverall,
    weights: weights
  },
  insights: {
    strengths: Array.isArray(aiResponse.insights?.strengths) ? 
      aiResponse.insights.strengths : [],
    issues: Array.isArray(aiResponse.insights?.issues) ? 
      aiResponse.insights.issues : [],
    suggestions: Array.isArray(aiResponse.insights?.suggestions) ? 
      aiResponse.insights.suggestions : []
  },
  adVariations: Array.isArray(aiResponse.adVariations) ? 
    aiResponse.adVariations : [],
  flags: {
    compliance: Array.isArray(aiResponse.flags?.compliance) ? 
      aiResponse.flags.compliance : [],
    performance: Array.isArray(aiResponse.flags?.performance) ? 
      aiResponse.flags.performance : [],
    optimization: Array.isArray(aiResponse.flags?.optimization) ? 
      aiResponse.flags.optimization : []
  },
  metadata: {
    creativeName: originalCreative.name,
    processingTimeMs: Date.now() - originalCreative.processingStartTime,
    analyzedAt: new Date().toISOString(),
    sessionId: sessionId,
    workflowVersion: 'complete-v1.0',
    aiModel: 'gpt-4o',
    analysisQuality: aiResponse.score?.overall ? 'full' : 'fallback'
  }
};

console.log(`‚úÖ [${sessionId}] Successfully processed and enhanced AI response`);

return {
  json: enhancedResponse
};

// Prepare final response for frontend
const creative = $json;
const sessionId = creative.metadata?.sessionId || 'unknown';

console.log(`‚úÖ [${sessionId}] Preparing final response for creative:`, creative.creativeId);

// Convert score to 1-10 scale for frontend compatibility
const scoreOutOf10 = Math.round(creative.score.overall / 10);

// Enhanced final response with comprehensive data
const finalResponse = {
  success: true,
  score: scoreOutOf10,
  aiScore: scoreOutOf10,
  analysis: `Creative scored ${creative.score.overall}/100 with strengths in ${creative.insights.strengths.slice(0, 2).join(', ')}. Areas for improvement: ${creative.insights.issues.slice(0, 2).join(', ')}.`, 
  analysisText: `Creative scored ${creative.score.overall}/100 with strengths in ${creative.insights.strengths.slice(0, 2).join(', ')}. Areas for improvement: ${creative.insights.issues.slice(0, 2).join(', ')}.`, 
  recommendations: creative.insights.suggestions,
  suggestions: creative.insights.suggestions,
  confidence: creative.score.overall / 100,
  confidenceScore: creative.score.overall / 100,
  dimensions: creative.score.dimensions,
  strengths: creative.insights.strengths,
  issues: creative.insights.issues,
  compliance: creative.flags.compliance,
  metadata: creative.metadata,
  processingTime: creative.metadata.processingTimeMs,
  completedAt: creative.metadata.analyzedAt,

  // AI analysis data
  adVariations: creative.adVariations,
  variationCount: creative.adVariations.length,
  optimizationFlags: creative.flags.optimization,
  performanceFlags: creative.flags.performance,

  // Enhanced metadata for feedback loop
  feedbackData: {
    originalScore: creative.score.overall,
    scoreBreakdown: creative.score.dimensions,
    analysisQuality: creative.metadata.analysisQuality,
    sessionId: sessionId,
    workflowVersion: creative.metadata.workflowVersion,
    timestamp: new Date().toISOString()
  }
};

console.log(`üéØ [${sessionId}] Final response prepared with ${creative.adVariations.length} ad variations`);
console.log(`üìä [${sessionId}] Score: ${scoreOutOf10}/10 (${creative.score.overall}/100)`);
console.log(`üöÄ [${sessionId}] Optimization opportunities: ${creative.flags.optimization.join(', ')}`);

return {
  json: finalResponse
};

// Safety node - ensures webhook always gets valid data
const data = $json;
const sessionId = data?.metadata?.sessionId || data?.sessionId || `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

console.log(`üîç [${sessionId}] SAFETY CHECK: Final data before webhook response:`);
console.log('Data type:', typeof data);
console.log('Data keys:', Object.keys(data || {}));
console.log('Full data:', JSON.stringify(data, null, 2));

// ALWAYS return valid JSON - never empty
if (!data || Object.keys(data).length === 0) {
  console.error(`‚ùå [${sessionId}] No data received - returning fallback response`);
  return {
    json: {
      status: 'success',
      message: 'Workflow executed successfully',
      sessionId: sessionId,
      timestamp: new Date().toISOString(),
      workflowVersion: 'complete-v1.0',
      debugInfo: 'Safety node provided fallback response',
      note: 'This is a fallback response - the main workflow may have failed',
      fallback: true
    }
  };
}

// If we have data, enhance it with safety metadata
const enhancedData = {
  ...data,
  _safetyMetadata: {
    processedBy: 'Safety Node',
    timestamp: new Date().toISOString(),
    sessionId: sessionId,
    safetyCheck: 'passed'
  }
};

console.log(`‚úÖ [${sessionId}] Safety check passed, proceeding to webhook response`);
return {
  json: enhancedData
};
