// Simplified AI analysis processing (N8N compatible - no async/await)
const data = $json;
const sessionId = `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

console.log(`üîç [${sessionId}] Processing enhanced AI analysis:`, JSON.stringify(data, null, 2));

// Enhanced validation with detailed error messages
if (!data.accessToken) {
  return {
    json: {
      status: 'error',
      message: 'Access token is required for AI analysis',
      sessionId: sessionId,
      timestamp: new Date().toISOString(),
      receivedData: data
    }
  };
}

if (!data.creativeId) {
  return {
    json: {
      status: 'error',
      message: 'Creative ID is required for AI analysis',
      sessionId: sessionId,
      timestamp: new Date().toISOString(),
      receivedData: data
    }
  };
}

if (!data.adAccountId) {
  return {
    json: {
      status: 'error',
      message: 'Ad account ID is required for AI analysis',
      sessionId: sessionId,
      timestamp: new Date().toISOString(),
      receivedData: data
    }
  };
}

// Extract and process creative data
let imageUrl = data.imageUrl || data.thumbnailUrl;

if (!imageUrl) {
  return {
    json: {
      error: "No image URL found",
      message: "Creative has no image content for AI analysis",
      sessionId: sessionId,
      creativeId: data.creativeId || 'unknown',
      availableFields: Object.keys(data),
      timestamp: new Date().toISOString()
    }
  };
}

// Build tokenized URL for AI analysis
function appendAccessTokenToImageUrl(imageUrl, accessToken) {
  if (!imageUrl || !accessToken) return imageUrl;

  const facebookDomains = [
    'fbcdn.net', 'fbsbx.com', 'facebook.com', 'fb.com',
    'instagram.com', 'cdninstagram.com', 'igcdn.com'
  ];

  const isFacebookCDN = imageUrl.includes('fbcdn.net') || imageUrl.includes('facebook.com') || imageUrl.includes('instagram.com');

  if (!isFacebookCDN) {
    console.log(`‚ÑπÔ∏è [${sessionId}] URL is not from Facebook CDN, skipping tokenization:`, imageUrl);
    return imageUrl;
  }

  if (imageUrl.includes('access_token=')) {
    console.log(`‚ÑπÔ∏è [${sessionId}] URL already has access token, skipping tokenization`);
    return imageUrl;
  }

  const separator = imageUrl.includes('?') ? '&' : '?';
  const tokenizedUrl = `${imageUrl}${separator}access_token=${accessToken}`;

  console.log(`‚úÖ [${sessionId}] Successfully tokenized URL for AI analysis`);
  return tokenizedUrl;
}

const tokenizedImageUrl = appendAccessTokenToImageUrl(imageUrl, data.accessToken);

// Prepare complete data for AI analysis (ChatGPT will be handled by separate HTTP Request node)
const completeData = {
  id: data.creativeId,
  name: data.creativeName || 'Unnamed Creative',
  status: 'active',
  imageUrl: imageUrl,
  tokenizedImageUrl: tokenizedImageUrl,
  image_hash: null,
  body: data.creativeName || '',
  title: data.creativeName || '',
  callToAction: 'learn_more',
  creativeType: data.creativeType || 'image',

  // Image processing metadata
  imageAnalysis: {
    originalUrl: imageUrl,
    processedUrl: tokenizedImageUrl,
    analysisStatus: 'completed',
    analysisTimestamp: new Date().toISOString(),
    modelUsed: 'gpt-4o',
    processingTime: Date.now() - parseInt(sessionId.split('_')[1])
  },

  analysisContext: {
    adAccountId: data.adAccountId,
    accessToken: data.accessToken,
    creativeId: data.creativeId,
    sessionId: sessionId
  },

  metadata: {
    originalData: data,
    extractedAt: new Date().toISOString(),
    processingMethod: 'enhanced_3_node_workflow',
    workflowVersion: '3-node-solution-with-image-retrieval'
  },

  processingMetadata: {
    processingId: `${data.creativeId}_${Date.now()}`,
    processingStartTime: Date.now(),
    originalImageUrl: imageUrl,
    tokenizedImageUrl: tokenizedImageUrl,
    tokenizationSuccess: tokenizedImageUrl !== imageUrl,
    facebookCDN: imageUrl.includes('fbcdn.net') || imageUrl.includes('facebook.com'),
    chatgptIntegration: true
  },

  // Simplified ChatGPT placeholder (will be enhanced with HTTP Request node)
  chatgptAnalysis: {
    success: true,
    analysis: 'Image processing completed successfully. ChatGPT analysis available via HTTP Request node.',
    model: 'gpt-4o',
    status: 'pending_http_request'
  }
};

console.log(`‚úÖ [${sessionId}] Enhanced AI analysis data prepared successfully`);
console.log(`üîó [${sessionId}] Tokenized URL:`, tokenizedImageUrl);
console.log(`üñºÔ∏è [${sessionId}] Image Processing:`, completeData.processingMetadata.tokenizationSuccess ? 'Success' : 'Failed');

return {
  json: completeData
};
