// Enhanced validation and data preparation for AI analysis
const data = $json;
const sessionId = `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

console.log(`üîç [${sessionId}] Processing AI analysis request:`, JSON.stringify(data, null, 2));

// Enhanced validation with detailed error messages
if (!data.accessToken) {
  return {
    json: {
      status: 'error',
      message: 'Access token is required for AI analysis',
      sessionId: sessionId,
      timestamp: new Date().toISOString(),
      receivedData: data
    }
  };
}

if (!data.creativeId) {
  return {
    json: {
      status: 'error',
      message: 'Creative ID is required for AI analysis',
      sessionId: sessionId,
      timestamp: new Date().toISOString(),
      receivedData: data
    }
  };
}

if (!data.adAccountId) {
  return {
    json: {
      status: 'error',
      message: 'Ad account ID is required for AI analysis',
      sessionId: sessionId,
      timestamp: new Date().toISOString(),
      receivedData: data
    }
  };
}

// Prepare enhanced data for AI analysis
const enhancedData = {
  ...data,
  sessionId: sessionId,
  workflowVersion: 'step-3-chatgpt-analysis',
  receivedAt: new Date().toISOString(),
  processingStartTime: Date.now(),
  analysisType: 'ai_creative_analysis'
};

console.log(`‚úÖ [${sessionId}] Validation passed, proceeding with AI analysis`);
console.log(`üîë Access token: ${enhancedData.accessToken.substring(0, 10)}...`);
console.log(`üéØ Creative ID: ${enhancedData.creativeId}`);
console.log(`üè¢ Ad Account ID: ${enhancedData.adAccountId}`);

return {
  json: enhancedData
};

// Process creative data directly from frontend without external API calls
const webhookData = $json;
const sessionId = webhookData.sessionId;

console.log(`üîç [${sessionId}] Processing creative data directly:`, JSON.stringify(webhookData, null, 2));

// Extract image URL for AI analysis
let imageUrl = webhookData.imageUrl || webhookData.thumbnailUrl;

if (!imageUrl) {
  return {
    json: {
      error: "No image URL found",
      message: "Creative has no image content for AI analysis",
      sessionId: sessionId,
      creativeId: webhookData.creativeId || 'unknown',
      availableFields: Object.keys(webhookData),
      timestamp: new Date().toISOString()
    }
  };
}

// Prepare data for AI analysis using frontend data
const aiAnalysisData = {
  id: webhookData.creativeId,
  name: webhookData.creativeName || 'Unnamed Creative',
  status: 'active',
  imageUrl: imageUrl,
  image_hash: null,
  body: webhookData.creativeName || '',
  title: webhookData.creativeName || '',
  callToAction: 'learn_more',
  creativeType: webhookData.creativeType || 'image',
  analysisContext: {
    adAccountId: webhookData.adAccountId,
    accessToken: webhookData.accessToken,
    creativeId: webhookData.creativeId,
    sessionId: sessionId
  },
  metadata: {
    originalData: webhookData,
    extractedAt: new Date().toISOString(),
    processingMethod: 'direct_frontend_data'
  }
};

console.log(`‚úÖ [${sessionId}] Successfully prepared data for AI analysis`);

return {
  json: aiAnalysisData
};

// Build tokenized URL for AI analysis
const creative = $json;
const sessionId = creative.analysisContext.sessionId;

console.log(`üîç [${sessionId}] Building tokenized URL for AI analysis:`, JSON.stringify(creative, null, 2));

if (!creative || !creative.imageUrl) {
  return {
    json: {
      error: "Missing creative data or image URL",
      message: "Cannot build tokenized URL without image URL",
      sessionId: sessionId,
      receivedData: creative,
      timestamp: new Date().toISOString()
    }
  };
}

if (!creative.analysisContext || !creative.analysisContext.accessToken) {
  return {
    json: {
      error: "Missing access token",
      message: "Cannot build tokenized URL without access token",
      sessionId: sessionId,
      creative: creative,
      timestamp: new Date().toISOString()
    }
  };
}

// Enhanced URL tokenization for Facebook CDN
function appendAccessTokenToImageUrl(imageUrl, accessToken) {
  if (!imageUrl || !accessToken) return imageUrl;

  const facebookDomains = [
    'fbcdn.net', 'fbsbx.com', 'facebook.com', 'fb.com',
    'instagram.com', 'cdninstagram.com', 'igcdn.com'
  ];

  const isFacebookCDN = facebookDomains.some(domain => imageUrl.includes(domain));

  if (!isFacebookCDN) {
    console.log(`‚ÑπÔ∏è [${sessionId}] URL is not from Facebook CDN, skipping tokenization:`, imageUrl);
    return imageUrl;
  }

  if (imageUrl.includes('access_token=')) {
    console.log(`‚ÑπÔ∏è [${sessionId}] URL already has access token, skipping tokenization`);
    return imageUrl;
  }

  const separator = imageUrl.includes('?') ? '&' : '?';
  const tokenizedUrl = `${imageUrl}${separator}access_token=${accessToken}`;

  console.log(`‚úÖ [${sessionId}] Successfully tokenized URL for AI analysis`);
  return tokenizedUrl;
}

const tokenizedImageUrl = appendAccessTokenToImageUrl(
  creative.imageUrl, 
  creative.analysisContext.accessToken
);

// Enhanced data for AI analysis
const enhancedData = {
  ...creative,
  tokenizedImageUrl: tokenizedImageUrl,
  processingMetadata: {
    processingId: `${creative.id}_${Date.now()}`,
    processingStartTime: Date.now(),
    originalImageUrl: creative.imageUrl,
    tokenizedImageUrl: tokenizedImageUrl,
    tokenizationSuccess: tokenizedImageUrl !== creative.imageUrl,
    facebookCDN: creative.imageUrl.includes('fbcdn.net') || creative.imageUrl.includes('facebook.com')
  }
};

console.log(`üîó [${sessionId}] Built tokenized URL:`, tokenizedImageUrl);
return {
  json: enhancedData
};

