// Simple validation and response
const webhookData = $json;
const sessionId = `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

console.log(`üîç [${sessionId}] Webhook data received:`, webhookData);

// Check required fields
if (!webhookData.accessToken || !webhookData.creativeId || !webhookData.adAccountId) {
  return {
    json: {
      success: false,
      error: "Missing required fields",
      message: "accessToken, creativeId, and adAccountId are required",
      sessionId: sessionId,
      timestamp: new Date().toISOString()
    }
  };
}

// Return success response
return {
  json: {
    success: true,
    message: "Webhook received successfully",
    sessionId: sessionId,
    receivedData: webhookData,
    timestamp: new Date().toISOString()
  }
};
